import React from "react";

class MovieItem extends React.Component {
  constructor() {
    super();

    this.state = {
      willWatch: false
    };
  }
  render() {
    // console.log(
    //   "MovieItem => this.state",
    //   this.state,
    //   "MovieItem => this.props",
    //   this.props
    // );
    const { movie } = this.props;
    return (
      // return <p> {movie.title} </p>;
      <div className="card">
        <img
          className="card-img-top"
          src={`https://image.tmdb.org/t/p/w500${movie.backdrop_path ||
            movie.poster_path}`}
          alt=""
        />
        <div className="card-body">
          {/* {console.log("I CAN NOT see this state => ",this.state)} */}
          <h6 className="card-title">{movie.title}</h6>
          <div className="d-flex justify-content-between align-items-center">
            <p className="mb-0">Rating: {movie.vote_average}</p>
          </div>

          <div className="d-flex justify-content-end align-items-center mt-3">
            <button
              type="button"
              className="btn btn-info btn-sm"
              style = {{background: this.state.willWatch ? "green" : ""}}
              onClick={() => {
                console.log("Clickkk", this.state.willWatch);
                this.state.willWatch
                  ? this.setState({
                      willWatch: false
                    })
                  : this.setState({
                      willWatch: true
                    });
              }}
            >
              {this.state.willWatch ? "NOT will watch" : "Will watch"}
            </button>
            <button
              type="button"
              title="{movie.title}"
              className="btn btn-danger btn-sm mx-2"
            //   onClick={removeMovie.bind(movie)}
            >
              <span className="material-icons">delete_forever</span>
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default MovieItem;
