import React from "react";
import { moviesData } from "../moviesData";
import MovieItem from "./MovieItem";



class App extends React.Component {
  constructor() {
    super();
    this.state = {
      myMovies: moviesData,
      willWatch: []
    }
  }

  removeMyMovie = myMovie => {
    const newMovies = this.state.myMovies.filter(function(item) {
       return item.id !== myMovie.id;
    });
    this.setState(
      {myMovies: newMovies}
    );
  };

  addMyMovie = myMovie => {
    console.log(myMovie);
    const newWillWatch = [...this.state.willWatch];
    newWillWatch.push(myMovie);
    this.setState(
        {willWatch: newWillWatch}
        );
  };

  render() {
  return (
      <div className="container">
        <div className="row">
          <div className="col-9">
            <div className="row">
              {this.state.myMovies.map( myMovie => {
                return(
                    <div className="col-6 mb-4" key={myMovie.id}>
                        <MovieItem myMovie={myMovie} removeMyMovie={this.removeMyMovie} addMyMovie={this.addMyMovie} />
                    </div>
                    );
              })}
            </div>
          </div>
          <div className="col-3">
            <p>Will watch: {this.state.willWatch.length}</p>
          </div>
        </div>
      </div>

  )
  }
}



