import React from "react";

class MovieTab extends React.Component {
    render() {
        const { sort_by, changeSortBy } = this.props;
        const handleClick = value => () => {changeSortBy(value)};

        return (
            <ul className="tabs nav nav-pills">
                <li className="nav-item">
                    <div className={`nav-link ${sort_by==="popularity.desc" ? "active" : ""}`}
                         onClick={handleClick("popularity.desc")}
                    >
                        Popularity desc
                    </div>
                </li>
                <li className="nav-item">
                    <div className={`nav-link ${sort_by==="revenue.desc" ? "active" : ""}`}
                         onClick={handleClick("revenue.desc")}
                    >
                        Revenue desc
                    </div>
                </li>
                <li className="nav-item">
                    <div className={`nav-link ${sort_by==="vote_average.desc" ? "active" : ""}`}
                         onClick={handleClick("vote_average.desc")}
                    >
                        Vote average
                    </div>
                </li>
            </ul>
        );
    }
}

export default MovieTab;
