  constructor() {
    super();
    this.state = {
      myMovies: moviesData
    }
  }
  render() {
    return(
      <div>
        {this.state.myMovies.map(function(myMovie){
        return <p>{this.state.myMovie.title}</p>
      })}
      </div>
    );
  }
}



